clc;
clear;
setdemorandstream(1);
[XL,XU]=bratley2_bound();  %获取函数的边界
problem.f=@bratley2; % 测试函数
bounds=[XL;XU];
problem.bounds=bounds;
%assignin('base','bound',bounds);
dim=size(bounds,2); %维度
pointnum=10*dim; %2*(dim+1);  %初始采样点的个数
S=LHD(XL,XU,pointnum); %采点方法得到采点的自变量值
Y=callobj(problem.f,S); %初始采样点的函数真值
krig=buildKRG(S,Y);


X = gridsamp([XL(1) XL(2);XU(1) XU(2)], 35);
[YX,MSE1] = predictor(X, krig); 
X1 = reshape(X(:,1),35,35);
X2 = reshape(X(:,2),35,35);
YX = reshape(YX, size(X1));

